import React, {Component} from 'react'

class Login extends Component {
    constructor(){
        super()

        this.state=({
            // users: [],
            loginUser: {
              email: '',
              password: ''
            },
            currentUser: {},
        })
    }

    validateForm(){
        // console.log('validate form')
        return (
            this.state.loginUser.email.length >0 && this.state.loginUser.password.length >0
        ) 
    }
    handleChange = (e) => {
        // console.log('handle Change')
        this.setState({
          loginUser:{
            ...this.state.loginUser,
            [e.target.name]: e.target.value
          }
        })
    }

    loginUser = async (e) => {
        e.preventDefault();
        console.log(this.state.loginUser)
        // const validatedForm = this.validateForm()
        // if(validatedForm){
        const login = await fetch('http://localhost:8080/auth/login', {
            method: 'POST',
            credentials: 'include',
            body: JSON.stringify(this.state.loginUser),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        console.log(login);
        const parsedLogin = await login.json()
        console.log('response from login: ', parsedLogin)
        this.setState({
          currentUser: parsedLogin.data
        })
        if(parsedLogin.status.message === 'User Logged In'){
            console.log('User logged in')
            this.props.history.push('/recipes')
        }

      }



    render() {
        return(
        <div className="loginModal">
        <div className="column">
          <h2 className="ui teal image header">
            <div className="content">
              LogIn
            </div>
          </h2>
          <form onSubmit={this.loginUser} >
        <label>
              <input type='text' name='email' placeholder="E-mail Address" onChange={this.handleChange}/>
          </label><br/>
          <label>
              <input type='text' name='password' placeholder="Password" onChange={this.handleChange}/>
          </label><br/>
          <button className="login" type='submit'>
                    Login
          </button>
        </form>
          <div className="logInMessage">
            New to us? <a href="/">Sign Up</a>
          </div>
        </div>
      </div>

    );
  }
}

export default Login
