import React from 'react'


const User = (props) => {
    console.log('props from App',props)


    return(
        <div className="modalcontainer">
                <div className="modal fade" id="editUserModal" role="dialog">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <button type="button" className="close" data-dismiss="modal">&times;</button>
                                <h1 className='modalHeader'>Edit Your Account</h1>
                            </div>
                        <div className="modal-body">
                            <form action="/users/<%=session.userId%>?_method=PUT" method="post">
                                <input className='formFields' placeholder={props.currentUser.email} type="email" name="email"/><br/>
                                <input className='formFields' placeholder="password" type="password" name="password"/><br/>
                                <input className='formFields' id="uploadButton" type='submit' value="Update Info!"/>
                            </form>
                        </div>
                        <section>
                            <a href="#deleteModal" data-toggle="modal" data-dismiss="modal" className="deleteButton">Delete Account</a> 
                        </section>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-default" data-dismiss="modal">Close</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default User

