import React, {Component} from 'react'

class Login extends Component {
    constructor(){
        super()

        this.state=({
            email: '',
            password: ''
        })
    }

    validateForm(){
        // console.log('validate form')

        return (
            this.state.email.length >0 && this.state.password.length >0
        ) 
    }
    handleChange = (e) => {
        // console.log('handle Change')
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const validatedForm = this.validateForm()
        if(validatedForm){
        const login = await fetch('http://localhost:8080/users', {
            method: 'POST',
            credentials: 'include',
            body: JSON.stringify(this.state),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        const parsedLogin = await login.json()
        console.log('response from login: ', parsedLogin)

        // if(parsedLogin.status.message === 'User Logged In'){
        //     console.log('User logged in')
        //     this.props.history.push('/recipes')
        // }

      }
    }


    render(){
        return(
        <div className="loginModal">
        <div className="column">
          <h2 className="ui teal image header">
            <div className="content">
              LogIn
            </div>
          </h2>
          <form onSubmit={this.handleSubmit} >
        <label>
              <input type='text' name='email' placeholder="E-mail Address" onChange={this.handleChange}/>
          </label><br/>
          <label>
              <input type='text' name='password' placeholder="Password" onChange={this.handleChange}/>
          </label><br/>
          <button className="login" type='submit'>
                    Login
          </button>
        </form>
          <div className="logInMessage">
            New to us? <a href="/">Sign Up</a>
          </div>
        </div>
      </div>

    );
  }
}

export default Login
