import React, {Component} from 'react'
// import { async } from 'q';
import RandomRecipe from '../RandomRecipe'
// import BreakfastRecipe from '../BreakfastRecipe'
// import LunchRecipe from '../LunchRecipe'
// import SideDishRecipe from '../SideDishRecipe'
// import DinnerRecipe from '../DinnerRecipe'
// import DessertRecipe from '../DessertRecipe'

class RecipeContianer extends Component {
    constructor(){
        super()

        this.state = {
            recipes: [],
            ingredients: [],
            instructions: [],

        }
    }

    componentDidMount(){
        this.getRandomRecipe()
        .then((data) => {
            this.setState({
                recipes: data,
                ingredients: data[0].extendedIngredients,
                instructions: data[0].analyzedInstructions[0].steps
            })
        })

    }

    getRandomRecipe = async ()=> {
        try{
            const randomRecipes = await fetch('https://api.spoonacular.com/recipes/random?apiKey=55fda1ea8d364f40bf13b71105d65880')
            const randomRecipesJson = await randomRecipes.json()
            console.log("recipe", randomRecipesJson.recipes)
            return randomRecipesJson.recipes
        }catch(err){
            console.log('get random recipes err: ',err)
            return err
        }
    }

    handleTermChange = (term) => {
        const url = `https://api.spoonacular.com/recipes/random?number=1&tags=${term}&apiKey=55fda1ea8d364f40bf13b71105d65880`
        console.log(term)

        fetch(url, (err,res) => {
            this.setState({
                recipes: data,
                ingredients: data[0].extendedIngredients,
                instructions: data[0].analyzedInstructions[0].steps
            })
        })
       console.log(this.state.gifs)
       this.getRandomRecipe()
    }



    render(){
        console.log("this.state", this.state)
        return(
            <div className="recipeContainer">
                <button type="button" value="Breakfast" onClick={this.handleTermChange(event.target.value)}>Breakfast</button><br/>
                <button type="button" value="Lunch" onClick={this.handleTermChange(event.target.value)}>Lunch</button><br/>
                <button type="button" value="Dinner" onClick={this.handleTermChange(event.target.value)}>Dinner</button><br/>
                <button type="button" value="Side Dish" onClick={this.handleTermChange(event.target.value)}>Side Dish</button><br/>
                <button type="button" value="Dessert" onClick={this.handleTermChange(event.target.value)}>Dessert</button>
                <RandomRecipe recipes={this.state.recipes} ingredients={this.state.ingredients} instructions={this.state.instructions} />
               {/* five buttons here. each will have a value/name equal to their dish type. will have an onclick */}

            </div>
        )
    }

}

export default RecipeContianer
