import React, {Component} from 'react'
// import { async } from 'q';
import RandomRecipe from '../RandomRecipe'
// import BreakfastRecipe from '../BreakfastRecipe'
// import LunchRecipe from '../LunchRecipe'
// import SideDishRecipe from '../SideDishRecipe'
// import DinnerRecipe from '../DinnerRecipe'
// import DessertRecipe from '../DessertRecipe'

class RecipeContianer extends Component {
    constructor(){
        super()

        this.state = {
            recipes: [],
            ingredients: [],
            instructions: [],
            url: ''

        }
    }

    // componentDidMount(){
    //     this.getRandomRecipe()
    //     .then((data) => {
    //         this.setState({
    //             recipes: data,
    //             ingredients: data[0].extendedIngredients,
    //             instructions: data[0].analyzedInstructions[0].steps
    //         })
    //     })

    // }

    getRandomRecipe = async ()=> {
        try{
            const randomRecipes = await fetch(this.state.url)
            const randomRecipesJson = await randomRecipes.json()
            console.log("recipe", randomRecipesJson.recipes)
            .then((data) => {
                this.setState({
                    recipes: data,
                    ingredients: data[0].extendedIngredients,
                    instructions: data[0].analyzedInstructions[0].steps
                })
            })
            return randomRecipesJson.recipes
        }catch(err){
            console.log('get random recipes err: ',err)
            return err
        }

    }

    handleTermChange = (e) => {
        const url = `https://api.spoonacular.com/recipes/random?number=1&tags=${e.target.value}&apiKey=55fda1ea8d364f40bf13b71105d65880`
        console.log("e: ",e)
        console.log("url: ",url)

        this.setState({
            url: url
        })
        // fetch(url, (err,res) => {
        //     this.setState({
        //         recipes: data,
        //         ingredients: data[0].extendedIngredients,
        //         instructions: data[0].analyzedInstructions[0].steps
        //     })
        // })
        this.getRandomRecipe()
       console.log(this.state.gifs)
    }



    render(){
        console.log("this.state", this.state)
        return(
            <div className="recipeContainer">
                <button type="button" value="breakfast" onClick={this.handleTermChange}>Breakfast</button><br/>
                <button type="button" value="lunch" onClick={this.handleTermChange}>Lunch</button><br/>
                <button type="button" value="dinner" onClick={this.handleTermChange}>Dinner</button><br/>
                <button type="button" value="side dish" onClick={this.handleTermChange}>Side Dish</button><br/>
                <button type="button" value="dessert" onClick={this.handleTermChange}>Dessert</button>
                <RandomRecipe recipes={this.state.recipes} ingredients={this.state.ingredients} instructions={this.state.instructions} />
               {/* five buttons here. each will have a value/name equal to their dish type. will have an onclick */}

            </div>
        )
    }

}

export default RecipeContianer
