import React, {Component} from 'react'


class User extends Component {
    constructor(props){
        super(props)

        this.state={
        currentUser: {
            id: '',
            email: '',
            password: ''        
        }
        }
    }

    setUser =() => {
        this.setState({
        currentUser: this.props.currentUser
    })
    }

    componentDidMount =()=> {
        this.setUser();
    }

    deleteAccount = async (e)=> {
        e.preventDefault();
        console.log('user from delete route: ', this.state.currentUser)

        try{
            const deleteRequest = await fetch ('http://localhost:8080/users/'+this.state.currentUser.id, {
                method: 'DELETE',
                credentials: 'include'
            })
            console.log("this.props.currentUser: ", this.props.currentUser)
            console.log("delete request: ", deleteRequest)
            if(deleteRequest.status !== 200){
                throw Error ("Delete Request Not Working")
            }
            this.setState({
                currentUser: ''
            })
            this.isLoggedin()
            // const deleteResponse = await deleteRequest.json()
            // console.log('delete response: ', deleteResponse)

        }catch (err){
            console.log("delete user err: ", err)
            return(err)
        }
        console.log("this.props.currentUser: ", this.props.currentUser)
    }

    isLoggedin = () => {
        if(this.props.currentUser){
            this.setState({
                currentUser: this.props.currentUser
            })  
            return(
                <div className="editInfo">
                <div className="editHeader">
                    <h1 className='editHeader'>Edit Your Account</h1>
                </div>
            <div className="editBody">
                <form action="/users/<%=session.userId%>?_method=PUT" method="post">
                    <input className="textBox" placeholder={this.state.currentUser.email} type="email" name="email"/><br/>
                    <input className='textBox' placeholder="********" type="password" name="password"/><br/>
                    <input className='login' id="uploadButton" type='submit' value="Update Info!"/>
                </form>
            </div><br/>
            <section>
                <button className="deleteButton" onClick={this.deleteAccount}>Delete Account</button>
            </section>
        </div>
            )
        } else {
            this.props.history.push('login')
        }
    }

    render(){
        console.log("currentUser: ", this.state.currentUser)
        return(
            <div>
             {this.isLoggedin()}
            </div>
        )
    }
}

export default User
