import React, {Component} from 'react'

class Register extends Component {
    constructor(){
        super()

        this.state={
            email: '',
            password: ''
        }
    }

    handleChange= (e) => {
        this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const register = await fetch('http://localhost:8080/auth/register', {
            method: 'POST',
            credentials: 'include',
            body: JSON.stringify(this.state),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        const parsedRegister = await register.json()
        console.log('response from register: ', parsedRegister)
        if(parsedRegister.status.message === 'User Logged In'){
            console.log('User logged in')
            this.props.history.push('/recipes')
        }
    }

    render(){
        return(
            <div className="register">
            <div className="content">
              Log-in to your account
            </div>
            <form onSubmit={this.handleSubmit} >
                <label>
                    <input type='text' name='email' value="email" onChange={this.handleChange}/>
                </label><br/>
                <label>
                    <input type='text' name='password' value="password" onChange={this.handleChange}/>
                </label><br/>
                <button type='submit'>
                    Register
                </button>
            </form>
            </div>
        )
    }

}

export default Register
