import React, {Component} from 'react'

class Login extends Component {
    constructor(){
        super()

        this.state=({
            email: '',
            password: ''
        })
    }

    validateForm(){
        // console.log('validate form')

        return (
            this.state.email.length >0 && this.state.password.length >0
        ) 
    }
    handleChange = (e) => {
        // console.log('handle Change')
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const validatedForm = this.validateForm()
        if(validatedForm){
        const login = await fetch('http://localhost:8080/auth/login', {
            method: 'POST',
            credentials: 'include',
            body: JSON.stringify(this.state),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        const parsedLogin = await login.json()
        console.log('response from login: ', parsedLogin)
        if(parsedLogin.status.message === 'User Logged In'){
            console.log('User logged in')
            this.props.history.push('/recipes')
        }
      }
    }


    render(){
        return(
        <div className="ui middle aligned center aligned grid, loginModal">
        <div className="column">
          <h2 className="ui teal image header">
            <div className="content">
              Log-in to your account
            </div>
          </h2>
          <form className="ui large form" onSubmit={this.handleSubmit}>
            <div className="ui stacked segment">
              <div className="field">
                <div className="ui left icon input">
                  <i className="user icon"></i>
                  <input type="text" name="email" placeholder="E-mail address" onChange={this.handleChange}/>
                </div>
              </div>
              <div className="field">
                <div className="ui left icon input">
                  <i className="lock icon"></i>
                  <input type="password" name="password" placeholder="Password" onChange={this.handleChange}/>
                </div>
              </div>
              <button className="login" type='submit'>
                    Login
                </button>
            </div>
            <div className="ui error message"></div>
          </form>
          <div className="ui message">
            New to us? <a href="/">Sign Up</a>
          </div>
        </div>
      </div>

    );
  }
}

export default Login
