import React, {Component} from 'react'
// import { async } from 'q';
import RandomRecipe from '../RandomRecipe'

class RecipeContianer extends Component {
    constructor(){
        super()

        this.state = {
            showButtons: true,
            showRecipeModal: false,
            recipes: [],
            ingredients: [],
            instructions: [],
            url: ''


        }
    }

    getRandomRecipe = async ()=> {
        try{
            const randomRecipes = await fetch(this.state.url)
            const randomRecipesJson = await randomRecipes.json()
            console.log("recipe", randomRecipesJson.recipes)
            return randomRecipesJson.recipes
        }catch(err){
            console.log('get random recipes err: ',err)
            return err
        }

    }

    handleTermChange = async (e) => {
        const fetchedRecipe = await fetch(`https://api.spoonacular.com/recipes/random?number=1&tags=${e.target.value}&apiKey=55fda1ea8d364f40bf13b71105d65880`)
        const fetchedRecipeJson = await fetchedRecipe.json()

        console.log("e: ",e)
        console.log("url: ",fetchedRecipeJson)

        this.setState({
            showButtons: false,
            showRecipeModal: true,
                recipes: await fetchedRecipeJson.recipes,
                ingredients: await fetchedRecipeJson.recipes[0].extendedIngredients,
                instructions: await fetchedRecipeJson.recipes[0].analyzedInstructions[0].steps
            })
    }

    render(){
        console.log("this.state", this.state)
        return(
            <div className="recipeContainer">
                {this.state.showRecipeModal ? <RandomRecipe recipes={this.state.recipes} ingredients={this.state.ingredients} instructions={this.state.instructions} /> : null}

                <div className="buttonContainer">
                <button className="button" type="button" style={{display: this.state.showButtons ? 'inline-block' : 'none' }} value="breakfast" onClick={this.handleTermChange}>Breakfast</button><br/>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'inline-block' : 'none' }} value="breakfast" onClick={this.handleTermChange}>Breakfast</button><br/>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'inline-block' : 'none' }} value="dinner" onClick={this.handleTermChange}>Dinner</button><br/>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'inline-block' : 'none' }} value="side dish" onClick={this.handleTermChange}>Side Dish</button><br/>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'inline-block' : 'none' }} value="dessert" onClick={this.handleTermChange}>Dessert</button>
                </div>

        </div>
        )
    }

}

export default RecipeContianer
