import React, {Component} from 'react'
// import { async } from 'q';
import RandomRecipe from '../RandomRecipe'

class RecipeContianer extends Component {
    constructor(){
        super()

        this.state = {
            // breakfastRecipes: [],
            // lunchRecipes: [],
            // dinnerRecipes: [], 
            recipes: [],
            ingredients: [],
            instructions: []
        }
    }

    componentDidMount(){
        // console.log("component did mount")
        // console.log('this.state.recipes',this.state.recipes)
        this.getRandomRecipe()
        .then((data) => {
            console.log("data[0]",data[0].extendedIngredients)
            console.log("data[0]",data[0].analyzedInstructions[0].steps)
            this.setState({
                recipes: data,
                ingredients: data[0].extendedIngredients,
                instructions: data[0].analyzedInstructions[0].steps
            })
        })
    }

    getRandomRecipe = async ()=> {
        try{
            const randomRecipes = await fetch('https://api.spoonacular.com/recipes/random?apiKey=55fda1ea8d364f40bf13b71105d65880')
            const randomRecipesJson = await randomRecipes.json()
            console.log("recipe", randomRecipesJson.recipes)
            return randomRecipesJson.recipes
        }catch(err){
            console.log('get random recipes err: ',err)
            return err
        }
    }

    getBreakfastRecipes = async ()=> {
        try{
            const breakfastRecipes = await fetch('https://api.spoonacular.com/recipes/random?number=1&tags=breakfast&apiKey=55fda1ea8d364f40bf13b71105d65880')
            const breakfastRecipesJson = await breakfastRecipes.json()
            return breakfastRecipesJson.recipes
        }catch(err){
            console.log('get breakfast recipes err: ',err)
            return err
        }
    }

    getLunchRecipes = async () => {
        try{
            const lunchRecipes = await fetch('https://api.spoonacular.com/recipes/random?number=1&tags=lunch&apiKey=55fda1ea8d364f40bf13b71105d65880')
            const lunchRecipesJson = await lunchRecipes.json()
            return lunchRecipesJson.recipes
        }catch(err){
            console.log('get lunch recipes err: ', err)
            return err
        }
    }

    getDinnerRecipes = async () => {
        try{
            const dinnerRecipes = await fetch('https://api.spoonacular.com/recipes/random?number=1&tags=dinner&apiKey=55fda1ea8d364f40bf13b71105d65880')
            const dinnerRecipesJson = await dinnerRecipes.json()
            return dinnerRecipesJson.recipes
        }catch(err){
            console.log('get dinner recipes err: ', err)
            return err
        }
    }

    getSideDishRecipes = async () => {
        try{
            const sideDishRecipes = await fetch('https://api.spoonacular.com/recipes/random?number=1&tags=dinner&apiKey=55fda1ea8d364f40bf13b71105d65880')
            const sideDishRecipesJson = await sideDishRecipes.json()
            return sideDishRecipesJson.recipes
        }catch(err){
            console.log('get side dish recipes err: ', err)
            return err
        }
    }

    getDessertRecipes = async () => {
        try{
            const dessertRecipes = await fetch('https://api.spoonacular.com/recipes/random?number=1&tags=dinner&apiKey=55fda1ea8d364f40bf13b71105d65880')
            const dessertRecipesJson = await dessertRecipes.json()
            return dessertRecipesJson.recipes
        }catch(err){
            console.log('get dessert recipes err: ', err)
            return err
        }
    }

    render(){
        console.log(this.state, 'state in render')
        return(
            <div className="recipeContainer">
                <RandomRecipe recipes={this.state.recipes} ingredients={this.state.ingredients} instructions={this.state.instructions} />
            </div>
        )
    }

}

export default RecipeContianer
