import React, {Component} from 'react'
// import { async } from 'q';
import RandomRecipe from '../RandomRecipe'
// import BreakfastRecipe from '../BreakfastRecipe'
// import LunchRecipe from '../LunchRecipe'
// import SideDishRecipe from '../SideDishRecipe'
// import DinnerRecipe from '../DinnerRecipe'
// import DessertRecipe from '../DessertRecipe'

class RecipeContianer extends Component {
    constructor(){
        super()

        this.state = {
            showButtons: true,
            showRecipeModal: false,
            recipes: [],
            ingredients: [],
            instructions: [],
            url: ''


        }
    }

    // componentDidMount(){
    //     this.getRandomRecipe()
    //     .then((data) => {
    //         this.setState({
    //             recipes: data,
    //             ingredients: data[0].extendedIngredients,
    //             instructions: data[0].analyzedInstructions[0].steps
    //         })
    //     })

    // }

    getRandomRecipe = async ()=> {
        try{
            const randomRecipes = await fetch(this.state.url)
            const randomRecipesJson = await randomRecipes.json()
            console.log("recipe", randomRecipesJson.recipes)
            return randomRecipesJson.recipes
        }catch(err){
            console.log('get random recipes err: ',err)
            return err
        }

    }

    handleTermChange = async (e) => {
        const fetchedRecipe = await fetch(`https://api.spoonacular.com/recipes/random?number=1&tags=${e.target.value}&apiKey=55fda1ea8d364f40bf13b71105d65880`)
        const fetchedRecipeJson = await fetchedRecipe.json()

        console.log("e: ",e)
        console.log("url: ",fetchedRecipeJson)

        this.setState({
            showButtons: false,
            showRecipeModal: true,
                recipes: fetchedRecipeJson.recipes,
                ingredients: fetchedRecipeJson.recipes[0].extendedIngredients,
                instructions: fetchedRecipeJson.recipes[0].analyzedInstructions[0].steps
            })
    }

    render(){
        console.log("this.state", this.state)
        return(
            <div className="recipeContainer">
                {this.state.showRecipeModal ? <RandomRecipe recipes={this.state.recipes} ingredients={this.state.ingredients} instructions={this.state.instructions} /> : null}
                <div className="buttonContainer">
                <button className="button" type="button" style={{display: this.state.showButtons ? 'inline-block' : 'none' }} value="breakfast" onClick={this.handleTermChange}>Breakfast</button>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'block' : 'none' }} value="breakfast" onClick={this.handleTermChange}>Breakfast</button>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'block' : 'none' }} value="dinner" onClick={this.handleTermChange}>Dinner</button>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'block' : 'none' }} value="side dish" onClick={this.handleTermChange}>Side Dish</button>
                <button className="button" type="button" style={{display: this.state.showButtons ? 'block' : 'none' }} value="dessert" onClick={this.handleTermChange}>Dessert</button>
{/* 
                {this.state.showButtons ? <button className="button" type="button" value="breakfast" onClick={this.handleTermChange}>Breakfast</button> : null} <br/>
                {this.state.showButtons ? <button className="button" type="button" value="lunch" onClick={this.handleTermChange}>Lunch</button> : null} <br/>
                {this.state.showButtons ? <button className="button" type="button" value="dinner" onClick={this.handleTermChange}>Dinner</button> : null} <br/>
                {this.state.showButtons ? <button className="button" type="button" value="side dish" onClick={this.handleTermChange}>Side Dish</button> : null} <br/>
                {this.state.showButtons ? <button className="button" type="button" value="dessert" onClick={this.handleTermChange}>Dessert</button> : null} */}
                </div>

        </div>
        )
    }

}

export default RecipeContianer
