import React, {Component} from 'react';
import './App.css';
// import Header from './Header'
import {Route, Switch} from 'react-router-dom'
import RecipeContainer from './RecipeContainer'
import Register from './Register'
import Login from './Login'
import FrontPage from './FrontPage';
import UserContainer from './UserContainer';


const My404 = () => {
  return(
    <div>
      'Sorry the page you requested was not found'
    </div>
  )
}

class App extends Component{

  constructor()
  {
    super()
    this.state =
    {
      //nothing here yet
    }
  }

  handleLogin = (currentUser) =>
  {
    this.setState(
      {
        currentUser: currentUser
      });
    console.log("this.state.currentUser from App.js: ",this.state.currentUser)
  }

  render()
  {
    return (
      <div className="App">
        {/* <Header/> */}
          <Switch>
              <Route exact path='/' component={FrontPage}/>
              <Route exact path='/register' component={Register} handleLogin={this.handleLogin}/>
              <Route exact path='/recipe' component={RecipeContainer} />
              <Route exact path='/login' render={<Login handleLogin={this.handleLogin}/>}/>
              <Route exact path='/user' component={UserContainer} currentUser={this.state.currentUser} />
              <Route component = {My404}/>
          </Switch>
      </div>
    );
  }
}

export default App;
